// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title RewardStore
 * @dev A simple contract that allows users to purchase digital products using MATIC
 */
contract RewardStore {
    address public owner;

    // Event emitted when a product is purchased
    event ProductBought(address indexed buyer, uint256 amount, string productId);

    /**
     * @dev Constructor that sets the owner of the contract
     */
    constructor() {
        owner = msg.sender;
    }

    /**
     * @dev Function to buy a product
     * @param productId The ID of the product being purchased
     */
    function buyProduct(string memory productId) public payable {
        // Require payment amount to be greater than 0
        require(msg.value > 0, "Payment amount must be greater than 0");

        // Emit event with buyer's address, amount, and product ID
        emit ProductBought(msg.sender, msg.value, productId);

        // Transfer the payment to the owner of the contract
        (bool success, ) = payable(owner).call{value: msg.value}("");
        require(success, "Transfer to owner failed");
    }

    /**
     * @dev Get contract balance (for testing purposes)
     * @return The balance of the contract
     */
    function getContractBalance() public view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Modifier that allows only the owner to execute a function
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can perform this action");
        _;
    }

    /**
     * @dev Function to withdraw funds from the contract (emergency use only)
     * @param amount The amount to withdraw
     */
    function withdraw(uint256 amount) public onlyOwner {
        require(amount <= address(this).balance, "Insufficient balance");
        payable(owner).transfer(amount);
    }
}

